//单笔显示界面，删除提示
function ViewDelete() {
    return confirm("您确定要删除吗？");
}

function Delete(gvName) {
    var table = document.getElementById(gvName);
    var hasChecked = false;
    for (var i = 1; i < table.rows.length; i++) {
        var cell = table.rows[i].cells[0];
        var chk = cell.getElementsByTagName("INPUT");
        if (chk[0].checked) {
            hasChecked = true;
            break;
        }
    }

    if (!hasChecked) {
        alert('请选择要删除的数据！');
        return false;
    }
    if (confirm('确定要删除选择的数据？', '删除提示')) {
        return true;
    }
    else {
        return false;
    }
}
 
function ViewEdit() {
    var ID = $("#txtID").val();
    location.href = FormID + "Edit.aspx?SubModuleCode=" + SubModuleCode + "&FormID=" + FormID +"&SqlCmd=" + SqlCmd + "&ID=" + ID;
    window.event.returnValue = false;
    return false;
}

//放弃按钮
function Cancel() {
    var message = "您确定要放弃吗？";
    if (confirm(message)) {
        var url = location.href;
        if (url.substring(url.length - 1, url.length) != "=")
            location.href = url.replace("Edit", "View");
        else
            location.href = url.replace("Edit", "s");
        window.event.returnValue = false;
    }
    return false;
}

function Back() {
    var url = location.href;
    location.href = url.replace("View", "s");
    return false;
}
function Refresh() {
    location.replace(location.href);
    return false;
}

//离开按钮
function Exit() {
    var message = "您确定要离开吗？";
    if (confirm(message))
        window.parent.removetab();
    return false;
}
 
//DataGrid全选
function selectAll(ctlName, bool) {
    var ctl = document.getElementById(ctlName);
    var checkbox = ctl.getElementsByTagName('input');

    for (var i = 0; i < checkbox.length; i++) {
        if (checkbox[i].type == 'checkbox' && !checkbox[i].disabled) {
            checkbox[i].checked = bool;
        }
    }
}

