function ChkDelMustNumericValue(dgvName, idName, msg) {
    var blnvalue = true;
    var ctls = $("[id$='" + dgvName + "']").find("[id$='" + idName + "']");
    if (ctls.length == 0) {
        alert("明细资料不能为空，请输入！");
        return false;
    }

    ctls.each(function () {
        if (parseFloat($(this).val()) == 0 || isNaN(parseFloat($(this).val()))) {
            alert(msg + "不能为空，请输入！");
            $(this).focus();
            blnvalue = false;
            return false;
        }
    });
    return blnvalue;

}

