function PadLeft(str, length) {
    if (str.length >= length) {
        return str;
    }
    else {
        return PadLeft("0" + str, length);
    }
}
function ReturnInt(str) {
    while (str.substring(0, 1) == "0") {
        str = str.substring(1);
    }
    return str;
}

