function SyDG_moveOnTd(td) {
    if (event.offsetX > td.offsetWidth - 10)
        td.style.cursor = 'w-resize';
    else
        td.style.cursor = 'default';
    if (td.mouseDown != null && td.mouseDown == true) {
        if (td.oldWidth + (event.x - td.oldX) > 0)
            td.width = td.oldWidth + (event.x - td.oldX);
        td.style.width = td.width;
        td.style.cursor = 'w-resize';

        table = td;
        while (table.tagName != 'TABLE') table = table.parentElement;
        table.width = td.tableWidth + (td.offsetWidth - td.oldWidth); table.style.width = table.width;
    }
}
function SyDG_downOnTd(td) {
    if (event.offsetX > td.offsetWidth - 10) {
        td.mouseDown = true;
        td.oldX = event.x;
        td.oldWidth = td.offsetWidth;
        table = td; while (table.tagName != 'TABLE') table = table.parentElement;
        td.tableWidth = table.offsetWidth;
    }
}



var self = new Object();
var down_flag = false;
var diffValue = 10; // 光标与单元格分割条之间的距离差值，作为判定范围
var left_right = 0; //左侧还是右侧
//参数divid: 表外的div层的id
function TableOnMouseDown(td) {
    // if (event.offsetX < td.offsetWidth - diffValue) { return; }
    if (!(event.offsetX < diffValue || (td.offsetWidth - event.offsetX < diffValue))) { return; } // 左侧 || 右侧
    if (event.offsetX < diffValue) left_right = 0; //光标距离单元格 左侧分割条近，则拉动左侧的条
    else left_right = 1; //否则|| 右侧  //光标距离单元格右侧分割条近，则拉动右侧的条


    //当前按下的th
    if (self.currentTH != null) {
        return;
    }
    down_flag = true;

    //鼠标点击处的object
    var obj = document.elementFromPoint(window.event.clientX, window.event.clientY);
    var objL = document.elementFromPoint(window.event.clientX - diffValue, window.event.clientY);

    if (obj.tagName.toLowerCase() == "th") {
        //                if (obj.previousSibling == null) return;
        //                if (objL.nextSibling != null)
        {
            // if (obj.getAttribute("id") != objL.nextSibling.getAttribute("id"))
            {
                if (left_right == 0)
                    obj = obj.previousSibling;
                else
                    obj = obj; // obj.nextSibling;

            }
            //                    else {
            //                        return;
            //                    }
        }

        if (obj.className == "fixed") {
            return;
        }

        //当前鼠标的X坐标
        self.currentX = window.event.clientX;
        //当前被点击的th中的div
        self.currentTH = obj.childNodes[0]; //鼠标按下时所在单元格

        //捕获th
        //self.currentTH.setCapture();
        self.currentTH.parentNode.setCapture();

        //dv主层的Y坐标
        var t = 0;
        var e = td;
        while (e = e.offsetParent) { t += e.offsetTop; }

        //创建currentLine
        if (self.currentLine == null) {
            self.currentLine = document.createElement("div");
            self.currentLine.style.position = "absolute";
            self.currentLine.style.borderLeftWidth = "2px"
            self.currentLine.style.width = "1px"
            self.currentLine.style.borderLeftColor = "red"; // "black";
            self.currentLine.style.borderLeftStyle = "dashed";
            self.currentLine.style.zIndex = 3000;
            self.currentLine.style.height = obj.parentNode.parentNode.offsetHeight;  //  th--> tr--> tbody
            self.currentLine.style.posTop = t;
            self.currentLine.style.posLeft = window.event.clientX - 1;
            window.document.body.insertAdjacentElement("afterBegin", self.currentLine);
        }
        else {
            self.currentLine.style.height = obj.parentNode.parentNode.offsetHeight;
            self.currentLine.style.posTop = t;
            self.currentLine.style.posLeft = window.event.clientX - 1;
            self.currentLine.style.visibility = "";
        }

        self.currentWidth = 0;
    }
}

function TableOnMouseUp(td) {
    down_flag = false;
    td.style.cursor = 'default';
    if (self.currentTH != null) {
        // if ((parseInt(self.currentTH.offsetWidth) + self.currentWidth) < 30) {//clientWidth
        //     self.currentTH.style.width = 30 + "px";
        // }
        // else
        {
            //改变th表头列的宽度
            var o = self.currentTH; //td
            var text = self.currentTH.nodeValue.toString(); //鼠标按下时所在单元格的内容

            o = td.parentNode; //tr
            var i = 0;
            var count = o.childNodes;
            while (o.childNodes[i].innerText != text) { i++; }

            //                    for ( var k = 0; k < count; k++) {
            //                        if (o.childNodes[k].innerText == text) break;
            //                    }

            if (i >= count) return; //超出列数
            o = o.childNodes[i];
            var w = parseInt(o.offsetWidth) + self.currentWidth; //clientWidth
            if (w < 10) w = 10;
            o.style.width = w;
        }

        //改变相关列的宽度
        //  resizeCell(tbodyid, self.currentTH.columnIndex, self.currentTH.style.width);
        var tbodyid = td.parentNode.parentNode;
        //resizeCell(tbodyid, i, o.style.width);


        //停止捕获
        //self.currentTH.releaseCapture();
        self.currentTH.parentNode.releaseCapture();
        self.currentTH = null;
        self.currentLine.style.visibility = "hidden";
        self.currentWidth = 0;
    }
}

function TableOnMouseMove(td) {
    if (down_flag == false) {
        if (event.offsetX < diffValue || (td.offsetWidth - event.offsetX < diffValue))// 左侧 || 右侧
            td.style.cursor = 'w-resize';
        else {
            td.style.cursor = 'default';
        }
    }
    if (self.currentTH != null) {
        var pos = Math.round(window.event.clientX - self.currentX);

        //当前被拖动的宽度
        self.currentWidth = pos;
        self.currentLine.style.posLeft = window.event.clientX - 1;
    }
}

function resizeCell(tid, idx, width) {
    //tbody
    var cells = tid; // document.getElementByIdx_x_x(tid);
    //tbody中的所有行
    var rows = cells.childNodes;

    for (var i = 0; i < rows.length; i++) {
        //tbody行中的要被改变宽度的列
        var cell = rows[i].childNodes[idx]; //.childNodes[0];
        var resetPattern = /style=[^\s\t\n]+/;
        cell.outerHTML = cell.outerHTML.toString().replace(resetPattern, "style='width:" + width + "'");
    }
}
//Download by http://www.codefans.net
//参数divid: 表外的div层的id
function IniCellWidth(divid) {
    var table = document.getElementById("Sub_container").childNodes[0];
    var childs = table.rows[0].cells;

    var wdt = 0;
    var cot = 0;

    //清除标题行每列th的宽度
    for (var i = 0; i < childs.length; i++) {
        if (childs[i].style.width != "") {
            childs[i].childNodes[0].style.width = childs[i].style.width;
            wdt += parseInt(childs[i].style.width);
            cot += 1;
            childs[i].style.width = "";
        }
    }

    var w = (table.clientWidth - wdt - parseInt(table.cellPadding) * childs.length * 2 - parseInt(table.cellPadding)) / (childs.length - cot);

    //parseInt(table.cellPadding) - parseInt(table.style.borderWidth)*2*childs.length

    if (w > 0) {
        //初始化表题行每列的宽度
        for (var i = 0; i < childs.length; i++) {
            if (childs[i].childNodes[0].style.width == "") {
                childs[i].childNodes[0].style.width = w + "px";
            }
        }
    }
    table.style.width = "";
}



